import React, { Component } from 'react';
import Button from '@material-ui/core/Button'
import DangerButton from './DangerButton'
import Grid from '@material-ui/core/Grid'
import AnswersComponent from './Answers'


const styles = ({
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: 'column'
  },
  root: {
    flexGrow: 1
  }
})

const MAX_NUM_QUESTIONS = 5

class QuestionsComponent extends Component {

  constructor(props) {
    super(props)

    this.state = {
      startGame: true,
      singlePlayer: true,
      duration: 1,
      questionIndex: 0,
      currentScore: 0,
    };

    this.nextQuestion = this.nextQuestion.bind(this)
  }

  componentWillMount() {
    this.onGetQuestions()
  }


  async onGetQuestions(category) {
    const finalCateg = category == null ? "entertainment-music" : category

    console.log("Starting out")
    //fetch questions
    const response = await fetch("https://cocktail-trivia-api.herokuapp.com/api/category/" + finalCateg)
    console.log("response is: ")
    console.log(response)


    const allData = await response.json()
    console.log("All Data: ")
    console.log(allData)


    this.setState({ questionsArr: allData })
    // this.setState({ currentQ: allData[this.state.questionIndex].text })
    // this.setState({ currentAnswers: allData[this.state.questionIndex].answers })

  }

  nextQuestion(isCorrect) {
    console.log("Callback answered: " + isCorrect);

    const score = isCorrect ? this.state.currentScore + 1 : this.state.currentScore
    const nextQIndex = this.state.questionIndex + 1

    console.log("The state is: ");
    console.log(this.state);

    // if (nextQIndex >= MAX_NUM_QUESTIONS) {
    //   // this.setState({gameOver: true})

    // } else {
      this.setState({ currentScore: score })
      this.setState({ questionIndex: nextQIndex })

    // }





  }




  render() {

    return (
      <Grid
        container
        direction="column"
        justify="center"
        alignItems="center"
      >

        <p>{this.state.questionsArr && this.state.questionsArr[this.state.questionIndex].text}</p>
        <AnswersComponent answers={this.state.questionsArr && this.state.questionsArr[this.state.questionIndex].answers} callback={this.nextQuestion}></AnswersComponent></Grid>)
  }

}
export default QuestionsComponent
