      // },
      emptyDic:[],

      //PREVIOUSLY:
      generalKnowledge: false,
      film: false,
      scienceNature: false,
      sports: false,
      history: false,
      tv: false,
      books: false,
      music: false,
      mythology: false,
      geography: false,
      art: false,
      videoGames: false,
    };

    // this.onClickGeneralKnowledge = this.onClickGeneralKnowledge.bind(this);
    // this.onClickFilm = this.onClickFilm.bind(this);
