import React, { Component } from 'react';
import Button from '@material-ui/core/Button'
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid'
const Entities = require('html-entities').AllHtmlEntities
import { styles } from '../stylesheet.js'

const entities = new Entities();


class FeedbackComponent extends Component {

  constructor(props) {
    super(props)
    this.onPressDismiss = this.onPressDismiss.bind(this)
  }

  onPressDismiss() {
    this.props.callback()
  }

  render() {
    let headerText;
    let bodyText;
    if (this.props.wasCorrect) {
      headerText = <p style={styles.feedbackHeaderTextCorrect}>Correct!</p>;
      bodyText = <p style={styles.feedbackBodyText}>Way to go! You were right! {this.props.correctAnswer} is correct.</p>;
    } else {
      headerText = <p style={styles.feedbackHeaderTextIncorrect}>Incorrect</p>;
      bodyText = <p style={styles.feedbackBodyText}>Oh no, that wasn't right. The correct answer was {this.props.correctAnswer}.</p>;
    }

    return (
      <div style={styles.feedbackContent}>
        <Grid columnGrid
        direction="column"
        justify="center"
        alignItems="center"
        spacing={2}>

          <Grid rowGrid spacing={1}>
            {headerText}
          </Grid>

          <p style={styles.feedbackBodyText}>{bodyText}</p>


        </Grid>
      </div>
    );


  }
}
export default FeedbackComponent;
