import React, { Component } from 'react'
import Button from '@material-ui/core/Button'
import DangerButton from './components/DangerButton'


const styles = ({
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: 'column'
  }
})

export default class IndexPage extends Component {
  constructor(props) {
    super(props)

    this.state = {
      startGame: true,
      singlePlayer: true,
      duration: 1
    };

    this.onClickStartGame = this.onClickStartGame.bind(this)
  }

  onClickStartGame() {
    //change the state when the user clicks start game
    if(this.state.startGame === true){
      this.setState({startGame: false})
    }else{
      this.setState({startGame: true})
    }
  }

  onClickSingle() {
    this.state.singlePlayer = true
  }

  onClickMulti() {
    this.state.singlePlayer = false
  }

  render() {

    //choose the button to display based on the state
    const startButton = this.state.startGame? <Button variant="contained" onClick={this.onClickStartGame} >
     Start Game</Button>: <DangerButton text="End Game" onClick={this.onClickStartGame}></DangerButton>

     const singlePlayerSelector = this.state.singlePlayer? <DangerButton text="Single Player"></DangerButton>:
      <Button variant="contained" onClick={this.onClickSingle} >Single Player</Button>

      const multiPlayerSelector = this.state.singlePlayer? <Button variant="contained" onClick={this.onClickMulti} >
      Multi Player</Button>:<DangerButton text="Multi Player"></DangerButton>


    return (
      <div style={styles.container}>
        {singlePlayerSelector}
        {startButton}
      </div>
    )
  }
}
