import React, { Component } from 'react'
import Button from '@material-ui/core/Button'
import DangerButton from './components/DangerButton'


const styles = ({
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: 'column'
  }
})

export default class IndexPage extends Component {
  constructor(props) {
    super(props)
    this.state = { startGame: true }

    this.onClickStartGame = this.onClickStartGame.bind(this)
  }

  onClickStartGame() {
    //change the state when the user clicks start
    this.setState({startGame: false})
  }
  render() {

    //choose the button to display based on teh state
    const displayButton = this.state.startGame? <Button variant="contained" onClick={this.onClickStartGame} >
     Start Game</Button>: <DangerButton text="End Game"></DangerButton>

    return (
      <div style={styles.container}>
        {displayButton}

      </div>
    )
  }
}
